import akka.actor.typed.{ActorRef, ActorSystem, Behavior, SupervisorStrategy}
import akka.actor.typed.scaladsl.Behaviors
import scala.concurrent.duration._
import scala.util.Random

object FlakyWorker {
  // Protocol for FlakyWorker
  sealed trait Command
  final case class DoWork(replyTo: ActorRef[String]) extends Command
  case object Fail extends Command

  def apply(): Behavior[Command] = Behaviors.receive { (context, message) =>
    message match {
      case DoWork(replyTo) =>
        if (Random.nextBoolean()) {
          context.log.info("FlakyWorker: Work succeeded")
          replyTo ! "Work completed successfully"
          Behaviors.same
        } else {
          context.log.info("FlakyWorker: Work failed, throwing exception")
          throw new RuntimeException("Simulated failure")
        }
      case Fail =>
        context.log.info("FlakyWorker: Received Fail command, throwing exception")
        throw new RuntimeException("Forced failure")
    }
  }
}

object WorkerSupervisor {
  def apply(): Behavior[FlakyWorker.Command] =
    Behaviors.supervise(FlakyWorker())
      .onFailure[RuntimeException](SupervisorStrategy.restartWithBackoff(1.second, 10.seconds, 0.2))
}

object WorkerApp {
  def main(args: Array[String]): Unit = {
    // Initialize the actor system with the supervised behavior
    val system: ActorSystem[FlakyWorker.Command] = ActorSystem(WorkerSupervisor(), "WorkerSystem")

    // Create a simple reply actor to receive responses
    val replyActor: ActorRef[String] = system.systemActorOf(
      Behaviors.receiveMessage[String] { msg =>
        println(s"Reply received: $msg")
        Behaviors.same
      },
      "replyActor"
    )

    // Import the system's dispatcher explicitly for scheduleOnce
    import system.dispatcher

    // Send a work request and a forced failure
    system ! FlakyWorker.DoWork(replyActor)
    system ! FlakyWorker.Fail

    // Schedule another work request after 5 seconds with an explicit dispatcher
    system.scheduler.scheduleOnce(5.seconds) {
      system ! FlakyWorker.DoWork(replyActor)
    }(system.dispatcher)

    Thread.sleep(15000)
    system.terminate()
  }
}

import akka.actor.typed.{ActorRef, ActorSystem, Behavior}
import akka.actor.typed.scaladsl.Behaviors
import akka.persistence.typed.PersistenceId
import akka.persistence.typed.scaladsl.{Effect, EventSourcedBehavior}
import scala.concurrent.duration._

object LoggerPersistentActor {
  // Commands
  sealed trait Command
  final case class LogMessage(message: String, replyTo: ActorRef[Ack]) extends Command
  final case class GetHistory(replyTo: ActorRef[History]) extends Command

  sealed trait Ack
  case object Logged extends Ack

  // Events
  sealed trait Event
  final case class MessageLogged(message: String) extends Event

  // State
  final case class State(messages: List[String]) {
    def updated(event: Event): State = event match {
      // Fixed: wrap the updated list in a new State instance
      case MessageLogged(msg) => State(messages :+ msg)
    }
  }
  object State {
    def empty: State = State(Nil)
  }

  final case class History(messages: List[String])

  def apply(entityId: String): Behavior[Command] =
    EventSourcedBehavior[Command, Event, State](
      persistenceId = PersistenceId.ofUniqueId(entityId),
      emptyState = State.empty,
      commandHandler = (state, command) => command match {
        case LogMessage(message, replyTo) =>
          Effect.persist(MessageLogged(message)).thenRun { _ =>
            replyTo ! Logged
          }
        case GetHistory(replyTo) =>
          replyTo ! History(state.messages)
          Effect.none
      },
      eventHandler = (state, event) => state.updated(event)
    )
}

object LoggerPersistentApp {
  def main(args: Array[String]): Unit = {
    val system: ActorSystem[LoggerPersistentActor.Command] = ActorSystem(LoggerPersistentActor("logger-1"), "PersistentLoggerSystem")

    // Create an ack receiver actor
    val ackReceiver: ActorRef[LoggerPersistentActor.Ack] = system.systemActorOf(
      Behaviors.receiveMessage[LoggerPersistentActor.Ack] { ack =>
        println("Ack received")
        Behaviors.same
      },
      "ackReceiver"
    )

    // Create a history receiver actor
    val historyReceiver: ActorRef[LoggerPersistentActor.History] = system.systemActorOf(
      Behaviors.receiveMessage[LoggerPersistentActor.History] { history =>
        println(s"History: ${history.messages.mkString(", ")}")
        Behaviors.same
      },
      "historyReceiver"
    )

    import system.dispatcher
    // Send log messages
    system ! LoggerPersistentActor.LogMessage("First log message", ackReceiver)
    system ! LoggerPersistentActor.LogMessage("Second log message", ackReceiver)

    // Request history after a delay with an explicit dispatcher
    system.scheduler.scheduleOnce(3.seconds) {
      system ! LoggerPersistentActor.GetHistory(historyReceiver)
    }(system.dispatcher)

    Thread.sleep(5000)
    system.terminate()
  }
}
